import { useParams } from "react-router-dom"
import { useState, useEffect } from "react"
import axios from "axios"

export default function Home() {
    const [clubsData, setClubsData] = useState([])

    useEffect( () => {
        let processing = true
        axiosFetchData(processing)
        return () => {
            processing = false
        }
    },[])

    const axiosFetchData = async(processing) => {
        await axios.get('http://localhost:4000/clubs')
        .then(res => {
            if (processing) {
                setClubsData(res.data)
            }
        })
        .catch(err => console.log(err))
    }

    // const Select = () => {
    //     for (let i = 0; i < clubsData.length; i++) {
    //         <li>
    //             <a href="/club/2" className="clubLink"><img className="clubImg" src={require('../img/club2.png')} alt={clubsData[i]["name"]}/></a>
    //             <br /><a href="/club/2" className="clubLink">{clubsData[i]["name"]}</a>
    //             <br /><p className="clubName">{clubsData[i]["shortMsg"]} </p>
    //         </li>
    //     }
    // }

    const params = useParams()
    let clubData = null

    switch (params.id) {
        case "1":
            clubData = clubsData[0]
            break

        case "2":
            clubData = {
                name: 'Cal Quidditch',
                image: '../img/club2.png',
                major: 'All',
                description: 'You might have seen us on Memorial Glade, passing Quaffles back and forth, clenching broomsticks between our legs. You might have heard the smack of dodgeballs striking their targets as our Beaters hone their skills, or seen our Seekers try to grab the Snitch off a player’s shorts. You might have accidentally been hit by a Bludger as you tried to enjoy a relaxing afternoon on the Glade (sorry).  But most importantly Cal Quidditch is more than a sports team; it’s a community. It has given both Harry Potter nerds and Muggles a place to connect and form lasting connections while having the time of their lives on and off the field. Come join us if you are looking for some life-long friends and if you feel like your experience at Berkeley is missing some magic.',
                deadline: '12/10/2023'
            }
            break

        default:
            break
    }


    return (
        <>
            <h1>Browse UC Berkeley's Student Organizations</h1>
            <ul className="clubBox">
                <li>
                    <a href="/club/1" className="clubLink"><img className="clubImg" src={require('../img/club1.png')} alt="Public Health Advocate"/></a>
                    <br /><a href="/club/2" className="clubLink">Public Health Advocate</a>
                    <br /><p className="clubName">Founded in 2005, the Public Health Advocate (PHA) is an undergraduate publication featuring current public health issues with local, national, and international perspectives.</p>
                </li>
                <li>
                    <a href="/club/2" className="clubLink"><img className="clubImg" src={require('../img/club2.png')} alt="Cal Quidditch"/></a>
                    <br /><a href="/club/2" className="clubLink">Cal Quidditch</a>
                    <br /><p className="clubName">Cal Quidditch is more than a sports team; it is a community. It has given both Harry Potter nerds and Muggles a place to connect and form lasting connections while having the time of their lives on and off the field. </p>
                </li>

                {/* <Select /> */}
                {
                    clubsData != null ?
                    <>
                        <li>
                            <a href="/club/1" className="clubLink"><img className="clubImg" src={require('../img/club1.png')} alt={clubsData.name}/></a>
                            <br /><a href="/club/2" className="clubLink">{clubsData["name"]}</a>
                            <br /><p className="clubShortMsg">{clubsData["shortMsg"]}</p>
                        </li>
                    </>
                    : ''
                }
            </ul>


        </>

    )
}
