import { useState, useEffect } from "react"
import axios from "axios"

function Contact() {
    const [email, setEmail] = useState('')
    const [name, setName] = useState('')
    const [shortMsg, setShortMsg] = useState('')
    const [longMsg, setLongMsg] = useState('')
    const [error, setError] = useState('')
    // const [selectData, setSelectData] = useState([])
    // const [selectValue, setSelectValue] = useState('')

    // useEffect( () => {
    //     let processing = true
    //     axiosFetchData(processing)
    //     return () => {
    //         processing = false
    //     }
    // },[])

    // const axiosFetchData = async(processing) => {
    //     await axios.get('http://localhost:4000/users')
    //     .then(res => {
    //         if (processing) {
    //             setSelectData(res.data)
    //         }
    //     })
    //     .catch(err => console.log(err))
    // }

    const axiosPostData = async() => {
        const postData = {
            email: email,
            name: name,
            shortMsg: shortMsg,
            longMsg: longMsg
            // website: selectValue
        }

        await axios.post('http://localhost:4000/contact/send', postData)
        .then(res => setError(<p className="success">{res.data}</p>))
    }

    // const SelectDropdown = () => {
    //     return (
    //         <select value={selectValue} onChange={(e) => setSelectValue(e.target.value)}>
    //             <option value="" key="none"> -- Select One -- </option>
    //             {
    //                 selectData?.map( (item) => (
    //                     <option value={item.website} key={item.website}>{item.website}</option>
    //                 ))
    //             }
    //         </select>
    //     )
    // }

    const handleSubmit = (e) => {
        e.preventDefault()

        if (!shortMsg || !longMsg) {
            setError(<p className="required">Description is empty. Please enter a description.</p>)
        } else {
            setError('')
            axiosPostData()
        }
    }

    return (
        <>
            <h1>Add Your Student Organization</h1>

            <form className="contactForm">
                <label>Contact Email</label>
                <input type="text" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                <input type="text" id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} />

                {/* <label>How Did You Hear About Us?</label>
                <SelectDropdown /> */}

                <label>Short Description</label>
                <textarea id="shortMsg" name="shortMsg" value={shortMsg} onChange={(e) => setShortMsg(e.target.value)}></textarea>
                <label>Long Description</label>
                <textarea id="longMsg" name="longMsg" value={longMsg} onChange={(e) => setLongMsg(e.target.value)}></textarea>

                {error}

                <button type="submit" onClick={handleSubmit}>Submit</button>
            </form>
        </>
    )
}

export default Contact
